scores = []modTime = []credit = []def cal(x,y):    scores.append(x*y)i = int(input("How many modules do you have:"))v = 0 while v != i:    modHours = int(input("How many hours do you spent on each :"))    modTime.append(modHours)    creditMod = float(input("What is the grade:"))    credit.append(creditMod)    cal(modHours,creditMod)    v += 1    gpa = float(sum(scores)/sum(modTime))if gpa > 4:    print("invaild GPA")else:    print("All calculated")print(gpa)                                    